E2Helper.Descriptions["shoot(e:v)"] = "Shoots a bullet from the entity in the direction dir and returns 1 if successfull"
E2Helper.Descriptions["setShootSpread(v2)"] = "Sets the spread for a shoot and returns 1 if successfull"
E2Helper.Descriptions["setShootTracer(s)"] = "Sets the tracer name for a shoot and returns 1 if successfull"
E2Helper.Descriptions["setShootAmmo(s)"] = "Sets the ammo type for a shoot and returns 1 if successfull"
E2Helper.Descriptions["setShootTraceNumber(n)"] = "Trace every n bullet and returns 1 if successfull"
E2Helper.Descriptions["setShootBulletNumber(n)"] = "Sets the number of bullets to shoot each shoot and returns 1 if successfull"
E2Helper.Descriptions["setShootHullSize(n)"] = "Sets the size of a bullet and returns 1 if successfull"
E2Helper.Descriptions["setShootDistance(n)"] = "Sets the distance at which can be shoot and returns 1 if successfull"
E2Helper.Descriptions["setShootForce(n)"] = "Sets the force of a shoot and returns 1 if successfull\nThis is only important on props or corpses"
E2Helper.Descriptions["setShootDamage(n)"] = "Sets the damage a bullet does to an entity or player and returns 1 if successfull"